mapper.select:
SELECT *
FROM ADMIN
WHERE ADMIN_ID = #{adminId} AND ADMIN_PASSWORD = #{adminPassword}

WITH DateRange AS ( SELECT TRUNC(SYSDATE) - LEVEL + 1 AS registration_date FROM DUAL
CONNECT BY LEVEL <= 7
) SELECT TO_CHAR(DateRange.registration_date, 'MM-DD') AS month_day, NVL(user_counts.daily_user_count, 0) AS daily_user_count FROM DateRange LEFT JOIN ( SELECT TO_CHAR(TRUNC(register_date), 'MM-DD') AS registration_date, COUNT(*) AS daily_user_count FROM users WHERE register_date >= TRUNC(SYSDATE) - 7 GROUP BY TO_CHAR(TRUNC(register_date), 'MM-DD') ) user_counts ON TO_CHAR(DateRange.registration_date, 'MM-DD') = user_counts.registration_date ORDER BY DateRange.registration_date;

SELECT rnum, USER_NUMBER, USER_ID, USER_PASSWORD, USER_NAME, USER_EMAIL, TO_CHAR(REGISTER_DATE, 'YYYY-MM-DD') AS REGISTER_DATE FROM ( SELECT ROWNUM AS rnum, USER_NUMBER, USER_ID, USER_PASSWORD, USER_NAME, USER_EMAIL, REGISTER_DATE FROM ( SELECT USER_NUMBER, USER_ID, USER_PASSWORD, USER_NAME, USER_EMAIL, REGISTER_DATE FROM USERS
AND USER_NAME LIKE '%' || #{searchVo.keyword} || '%'
AND USER_ID LIKE '%' || #{searchVo.keyword} || '%'
ORDER BY USER_NUMBER DESC ) where rownum<=#{criteria.page} * #{criteria.amount}
)where rnum> (#{criteria.page}-1)*#{criteria.amount}

SELECT rnum, EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, TO_CHAR(EMP_DATE, 'YYYY-MM-DD') AS EMP_DATE, EMP_CONTENT FROM ( SELECT ROWNUM AS rnum, EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, EMP_DATE, EMP_CONTENT FROM ( SELECT EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, EMP_DATE, EMP_CONTENT FROM EMP
AND EMP_NAME LIKE '%' || #{searchVo.keyword} || '%'
AND EMP_EMAIL LIKE '%' || #{searchVo.keyword} || '%'
ORDER BY EMP_NUMBER DESC ) WHERE rownum <= #{criteria.page} * #{criteria.amount}
) WHERE rnum > (#{criteria.page} - 1) * #{criteria.amount}
