mapper.select:
SELECT RNUM,SITTER_BOARD_NUMBER, SITTER_BOARD_CONTENT, SITTER_BOARD_REGISTER_DATE,
       SITTER_BOARD_MODIFY_DATE, RATING, SITTER_BOARD_COUNT, USER_NUMBER, EMP_NUMBER, MATCH_NUMBER
FROM(
    select ROWNUM AS RNUM,SITTER_BOARD_NUMBER, SITTER_BOARD_CONTENT, SITTER_BOARD_REGISTER_DATE,
       SITTER_BOARD_MODIFY_DATE, RATING, SITTER_BOARD_COUNT, USER_NUMBER, EMP_NUMBER, MATCH_NUMBER
    from(
        select SITTER_BOARD_NUMBER, SITTER_BOARD_CONTENT, SITTER_BOARD_REGISTER_DATE,
        SITTER_BOARD_MODIFY_DATE, RATING, SITTER_BOARD_COUNT, USER_NUMBER, EMP_NUMBER, MATCH_NUMBER
        from SITTER_BOARD
        where USER_NUMBER=#{userNumber}
        )

    WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}

)
WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

select ec.est_title
from (
         select MATCH_NUMBER, USER_NUMBER, SUBMIT_ORDER_NUMBER, EMP_NUMBER, STATUS
         from MATCHS
         where MATCH_NUMBER = #{matchNumber}
     )s1
join EST_CONTENT ec on s1.match_number = ec.match_number

select count(SITTER_BOARD_NUMBER)
from SITTER_BOARD
where user_number =#{userNumber}
