mapper.select:
SELECT COUNT( distinct e.EMP_NUMBER) FROM EMP e join COUNTRY c on e.COUNTRY_NUMBER = c. COUNTRY_NUMBER join CITY c2 on c.CITY_NUMBER = c2.CITY_NUMBER left join EMP_ACT_ITEM eai on e.EMP_NUMBER = eai.EMP_NUMBER
c.COUNTRY_NAME like '%' || #{countryName} || '%' AND c2.CITY_NAME like '%' || #{cityName} || '%'
AND eai.ACT_NUMBER = #{actNumber}

SELECT RNUM, EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, EMP_DATE, EMP_CONTENT, avg,EMP_IMG_UPLOAD_PATH, EMP_IMG_UUID, EMP_IMG_NAME FROM ( SELECT ROWNUM AS RNUM, EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, EMP_DATE, EMP_CONTENT, avg ,EMP_IMG_UPLOAD_PATH, EMP_IMG_UUID, EMP_IMG_NAME FROM( SELECT distinct e.EMP_NUMBER, e.EMP_NAME, e.EMP_AGE, e.EMP_GENDER, e.EMP_PHONE, e.EMP_EMAIL, e.EMP_DATE, e.EMP_CONTENT, s1.avg , c.COUNTRY_NAME, c2.CITY_NAME , ei.EMP_IMG_UPLOAD_PATH, ei.EMP_IMG_UUID, ei.EMP_IMG_NAME FROM EMP e left join( select EMP_NUMBER, avg(RATING) avg from SITTER_BOARD sb group by EMP_NUMBER )s1 on e.EMP_NUMBER = s1.EMP_NUMBER join COUNTRY c on e.COUNTRY_NUMBER = c. COUNTRY_NUMBER join CITY c2 on c.CITY_NUMBER = c2.CITY_NUMBER left join EMP_ACT_ITEM eai on e.EMP_NUMBER = eai.EMP_NUMBER left join EMP_IMG ei on e.EMP_NUMBER = ei.EMP_NUMBER
c.COUNTRY_NAME like '%' || #{countryName} || '%' AND c2.CITY_NAME like '%' || #{cityName} || '%'
AND eai.ACT_NUMBER = #{actNumber}
ORDER BY EMP_NUMBER DESC ) X
WHERE ROWNUM <= #{criteria.page} * #{criteria.amount}
) Y WHERE RNUM > (#{criteria.page} - 1) * #{criteria.amount}

SELECT RNUM, EMP_NUMBER, EMP_NAME, EMP_AGE, EMP_GENDER, EMP_PHONE, EMP_EMAIL, EMP_DATE, EMP_CONTENT, avg FROM ( SELECT ROWNUM AS RNUM, e.EMP_NUMBER, e.EMP_NAME, e.EMP_AGE, e.EMP_GENDER, e.EMP_PHONE, e.EMP_EMAIL, e.EMP_DATE, e.EMP_CONTENT, s1.avg FROM EMP e left join( select EMP_NUMBER, avg(RATING) avg from SITTER_BOARD sb group by EMP_NUMBER ) s1 on e.EMP_NUMBER = s1.EMP_NUMBER
WHERE ROWNUM <= #{page} * #{amount} AND EMP_GENDER = 'F'
ORDER BY EMP_NUMBER DESC ) WHERE RNUM > (#{page} - 1) * #{amount}
